import React, { useState } from 'react';
import './SudokuGrid.css';
import axios from 'axios';

const SudokuGrid = () => {
  const [grid, setGrid] = useState(createEmptyGrid());

  // Function to create an empty Sudoku grid
  function createEmptyGrid() {
    return Array.from({ length: 9 }, () => Array(9).fill(''));
  }

  // Function to handle user input in cells
  function handleCellInput(event, row, col) {
    const value = event.target.value;

    // Allow only numbers 1-9 or an empty string (for deletion)
    if (/^[1-9]?$/.test(value)) {
      const newGrid = [...grid];
      newGrid[row][col] = value;
      setGrid(newGrid);
    }
  }

  // Function to handle difficulty selection
  async function handleDifficulty(difficulty) {
    try {
      const response = await axios.get(`http://localhost:3001/api/generate-puzzle/${difficulty}`);
      const puzzle = response.data.puzzle;
      setGrid(puzzle); // Assuming the response contains the puzzle grid
    } catch (error) {
      console.error('Error fetching Sudoku puzzle:', error);
    }
  }

  // JSX for rendering the Sudoku grid
  return (
    <div className="sudoku-container">
      <div className="sudoku-grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="row">
            {row.map((cell, colIndex) => (
              <input
                key={colIndex}
                type="text"
                maxLength={1}
                className="cell"
                value={cell}
                onChange={(event) => handleCellInput(event, rowIndex, colIndex)}
              />
            ))}
          </div>
        ))}
      </div>
      <div className="button-container">
        <button className="difficulty-button easy" onClick={() => handleDifficulty('easy')}>
          Easy
        </button>
        <button className="difficulty-button medium" onClick={() => handleDifficulty('medium')}>
          Medium
        </button>
        <button className="difficulty-button hard" onClick={() => handleDifficulty('hard')}>
          Hard
        </button>
      </div>
    </div>
  );
};

export default SudokuGrid;
